var Say = function(){};

// define colors
var r = "\033[31m";
var g = "\033[32m";
var b = "\033[34m";
var d = "\033[39m";

// define log message
var success = '[ ' + g + 'ok' + d + ' ] ';
var fail = '[ ' + r + 'fail' + d + ' ] ';

Say.prototype.success = function (req) {
    console.log(success + req);
}

Say.prototype.fail = function (req) {
    console.log(fail + req);
}


module.exports = Say;